positions.value:
var positions = [];
console.log("dept is " + $data.formInput.newEmployeeInput.department);
switch($data.formInput.newEmployeeInput.department) {
    case "Facilities Management":
        positions.push("Supervisor");
        positions.push("Porter");
        positions.push("Electrician");
        return positions;

    case "Accounting":
        positions.push("Analyst");
        positions.push("Financial Officer");
        positions.push("Audit Specialist");
        return positions;

    case "IT Department":
        positions.push("Sysadmin");
        positions.push("Developer");
        positions.push("Project Manager");
        return positions;

    case "Telecom Services":
        positions.push("Field Service Engineer");
        positions.push("Handheld Technician");
        positions.push("AV Ops");
        return positions;

    default:
        positions.push("");
        return positions;
}
